mapper.insert:
INSERT INTO tb_blog_post (
				    POST_NUM,
				    POST_CATE_NUM,
				    BLOGER_ID,
				    POST_TITLE,
				    POST_CONTENT,
				    POST_DATE,
				    POST_PUBLIC_YN,
				    POST_TAG,
				    THUMNAIL
				) VALUES (
				    fn_create_num(to_char(sysdate,'yymmdd'), 'TB_BLOG_POST', 'POST_NUM', UPPER('blpo'))
				    , #{postCateNum,jdbcType=VARCHAR}
				    , #{blogerId,jdbcType=VARCHAR}
				    , #{postTitle,jdbcType=VARCHAR}
				    , #{postContent,jdbcType=CLOB}
				    , SYSDATE
				    , #{postPublicYn,jdbcType=VARCHAR}
				    , #{postTag,jdbcType=VARCHAR}
				    , #{thumnail, jdbcType=VARCHAR}
				)

mapper.select:
	SELECT COUNT(*)
     FROM TB_POSTMARK
    WHERE MEM_ID = #{detailCondition.memId}

mapper.insert:
INSERT INTO TB_POSTMARK (
		    POSTMARK_NUM,
		    POST_NUM,
		    MEM_ID
		) VALUES (
		  fn_create_num(to_char(sysdate,'yymmdd'), 'TB_POSTMARK', 'POSTMARK_NUM', UPPER('mark'))
		  , #{postNum,jdbcType=VARCHAR}
		  , #{memId,jdbcType=VARCHAR}
		)

mapper.delete:
DELETE FROM TB_POSTMARK
   	  WHERE POST_NUM = #{postNum,jdbcType=VARCHAR}
        AND MEM_ID =#{memId,jdbcType=VARCHAR}

mapper.update:
	UPDATE TB_BLOG_POST
	   SET
    POST_TITLE = #{postTitle,jdbcType=VARCHAR}
  , POST_CONTENT = #{postContent,jdbcType=CLOB}
  , POST_PUBLIC_YN = #{postPublicYn,jdbcType=VARCHAR}
  , POST_TAG = #{postTag,jdbcType=VARCHAR}
  , THUMNAIL = #{thumnail, jdbcType=VARCHAR}
 WHERE  POST_NUM = #{postNum,jdbcType=VARCHAR}

mapper.select:
SELECT COUNT(*)
  FROM TB_BLOG_HEART
 WHERE POST_NUM =#{postNum,jdbcType=VARCHAR}

