	, MEM_NAME
	, MEM_NICK  
	, MEM_MAIL 
	, MEM_HP 
	, ACCUM_REP
) VALUES (                                                                       
	 , #{memId,jdbcType=VARCHAR}
	, #{memPass,jdbcType=VARCHAR}
	, #{memName,jdbcType=VARCHAR}
	, #{memNick,jdbcType=VARCHAR}
	, #{memMail,jdbcType=VARCHAR}
	, #{memHp,jdbcType=VARCHAR}
	, #{accumRep,jdbcType=NUMERIC}
)

mapper.update:
UPDATE TB_MEMBER
SET                                                      
	MEM_ID     = #{memId,jdbcType=VARCHAR}          
	, MEM_PASS = #{memPass,jdbcType=VARCHAR} 
	, MEM_NAME = #{memName,jdbcType=VARCHAR} 
	, MEM_NICK = #{memNick,jdbcType=VARCHAR} 
	, MEM_MAIL = #{memMail,jdbcType=VARCHAR} 
	, MEM_HP   = #{memHp,jdbcType=VARCHAR}   
	, ACCUM_REP= #{accumRep,jdbcType=NUMERIC}
