mapper.insert:

	SELECT NVL(MAX(ATT_NO), 0) + 1
	FROM ATTATCH 

INSERT ALL

INTO attatch (
	ATT_NO
	, BO_NO
	, ATT_FILENAME
	, ATT_SAVENAME
	, ATT_MIME
	, ATT_FILESIZE
	, ATT_FANCYSIZE
	) VALUES (
	#{startAttNo} + #{idx}
	, #{boNo,jdbcType=NUMERIC}
	, #{attatch.attFilename,jdbcType=VARCHAR}
	, #{attatch.attSavename,jdbcType=VARCHAR}
	, #{attatch.attMime,jdbcType=VARCHAR}
	, #{attatch.attFilesize,jdbcType=NUMERIC}
	, #{attatch.attFancysize,jdbcType=VARCHAR}) 

SELECT * FROM DUAL

mapper.update:
UPDATE ATTATCH
SET ATT_DOWNLOAD = ATT_DOWNLOAD + 1
WHERE ATT_NO = #{attNo}

mapper.delete:
DELETE FROM ATTATCH
WHERE ATT_NO IN

	#{attNo}
